USE unidb;
START TRANSACTION;
SET time_zone = "+00:00";

CREATE TABLE `Baum` (
  `Server_ID` int(11) NOT NULL DEFAULT 2,
  `Baum_ID` int(11) NOT NULL,
  `Eltern_ID` varchar(30) NOT NULL,
  `Path` varchar(1024) NOT NULL,
  `owner` int(11) NOT NULL,
  `Bezeichnung` varchar(255) NOT NULL,
  `Vorlage` int(11) DEFAULT NULL,
  `Inhalt` mediumblob DEFAULT NULL,
  `Timestamp` timestamp NOT NULL DEFAULT current_timestamp() ON UPDATE current_timestamp(),
  `geloescht` tinyint(4) NOT NULL DEFAULT 0
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

CREATE TABLE `Baumhistorie` (
  `Hist_ID` int(11) NOT NULL,
  `Server_ID` int(11) NOT NULL DEFAULT 1,
  `Baum_ID` int(11) NOT NULL,
  `Eltern_ID` varchar(30) NOT NULL,
  `Path` varchar(1024) NOT NULL,
  `owner` int(11) NOT NULL,
  `Bezeichnung` varchar(255) NOT NULL,
  `Vorlage` int(11) DEFAULT NULL,
  `Inhalt` mediumblob DEFAULT NULL,
  `Timestamp` timestamp NOT NULL DEFAULT current_timestamp() ON UPDATE current_timestamp()
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

CREATE TABLE `Bausteine` (
  `Baustein_ID` int(11) NOT NULL,
  `User_ID` int(11) NOT NULL,
  `Bezeichnung` varchar(256) NOT NULL,
  `Inhalt` blob NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

CREATE TABLE `Eingangspuffer` (
  `Puffer_ID` int(11) NOT NULL,
  `Timestamp` datetime NOT NULL DEFAULT current_timestamp(),
  `Server` varchar(255) NOT NULL,
  `SQL_Text` varchar(2048) NOT NULL,
  `Typen` varchar(30) NOT NULL,
  `Ar` text NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;


CREATE TABLE `Einstellungen` (
  `Einstellung_ID` int(11) NOT NULL,
  `Eltern_ID` int(11) NOT NULL DEFAULT 0,
  `Parameter` varchar(255) NOT NULL,
  `Wert` varchar(255) NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;


CREATE TABLE `Elementvorlagen` (
  `Elementvorlage_ID` int(11) NOT NULL,
  `DE` varchar(255) NOT NULL,
  `EN` varchar(255) DEFAULT NULL,
  `NL` varchar(255) DEFAULT NULL,
  `FR` varchar(255) DEFAULT NULL,
  `Auswahl` tinyint(1) NOT NULL DEFAULT 1,
  `Bericht` tinyint(1) NOT NULL DEFAULT 0,
  `Formular` tinyint(1) NOT NULL DEFAULT 1,
  `Bild` tinyint(1) NOT NULL DEFAULT 0,
  `kann_Eltern` tinyint(1) NOT NULL DEFAULT 0
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;


CREATE TABLE `Hilfe` (
  `Hilfe_ID` int(11) NOT NULL,
  `Eltern_ID` int(11) NOT NULL DEFAULT 0,
  `Dokument` char(255) DEFAULT NULL,
  `Dialog` char(255) DEFAULT NULL,
  `Titel_DE` varchar(255) DEFAULT NULL,
  `DE` text DEFAULT NULL,
  `Titel_EN` varchar(255) DEFAULT NULL,
  `EN` text DEFAULT NULL,
  `Titel_NL` varchar(255) DEFAULT NULL,
  `NL` text DEFAULT NULL,
  `Titel_FR` varchar(255) DEFAULT NULL,
  `FR` text DEFAULT NULL,
  `Zeitstempel` timestamp NOT NULL DEFAULT current_timestamp() ON UPDATE current_timestamp(),
  `aktiv` tinyint(1) NOT NULL DEFAULT 1
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;


CREATE TABLE `Notizen` (
  `Notizen_ID` int(11) NOT NULL,
  `Betreff` varchar(255) DEFAULT NULL,
  `Notiz` text DEFAULT NULL,
  `Status` varchar(15) NOT NULL DEFAULT 'offen',
  `Aufwand` float DEFAULT NULL,
  `Prioritaet` int(11) NOT NULL DEFAULT 0
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

CREATE TABLE `Puffer` (
  `Puffer_ID` int(11) NOT NULL,
  `Timestamp` datetime NOT NULL DEFAULT current_timestamp(),
  `Server` varchar(255) NOT NULL,
  `SQL_Text` varchar(2048) NOT NULL,
  `Typen` varchar(30) NOT NULL,
  `Ar` text NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

CREATE TABLE `Themen` (
  `Themen_ID` int(11) NOT NULL,
  `Thema` varchar(255) NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;


CREATE TABLE `Themen_Parameter` (
  `Themen_Parameter_ID` int(11) NOT NULL,
  `Themen_ID` int(11) NOT NULL,
  `Klasse` varchar(255) NOT NULL,
  `Parameter` varchar(255) NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;


CREATE TABLE `Themen_Parameter_Werte` (
  `Themen_Parameter_Werte_ID` int(11) NOT NULL,
  `Themen_Parameter_ID` int(11) NOT NULL,
  `Wert` varchar(255) NOT NULL DEFAULT ''
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;


CREATE TABLE `Translation` (
  `T_ID` int(11) NOT NULL,
  `Dokument` varchar(255) NOT NULL,
  `Nummer` int(11) NOT NULL,
  `Text_DE` text NOT NULL,
  `Text_EN` text NOT NULL,
  `Text_NL` text NOT NULL,
  `Text_FR` text NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;


CREATE TABLE `User` (
  `User_ID` mediumint(8) UNSIGNED NOT NULL,
  `UserName` varchar(100) NOT NULL,
  `Password` varchar(32) NOT NULL,
  `Password_extension` varchar(10) NOT NULL,
  `ip` varchar(15) NOT NULL,
  `User_info` varchar(255) NOT NULL,
  `login` varchar(32) NOT NULL,
  `last_active` datetime NOT NULL,
  `mistrials` tinyint(3) UNSIGNED NOT NULL,
  `activated` tinyint(3) UNSIGNED NOT NULL,
  `Full_Name` varchar(255) NOT NULL,
  `eMail` varchar(255) NOT NULL,
  `Thema` varchar(255) NOT NULL DEFAULT 'default',
  `Admin` tinyint(4) NOT NULL DEFAULT 0,
  `Zeitstempel` timestamp NOT NULL DEFAULT current_timestamp(),
  `Sprache` varchar(2) NOT NULL DEFAULT 'DE',
  `Passwortcode` varchar(255) DEFAULT NULL,
  `Passwortcode_Zeit` timestamp NULL DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;


CREATE TABLE `Userdef_Filter` (
  `Userdef_Filter_ID` int(11) NOT NULL,
  `User_ID` int(11) NOT NULL,
  `Baum_ID` int(11) NOT NULL,
  `Server_ID` int(11) NOT NULL DEFAULT 2,
  `Filtername` char(50) NOT NULL,
  `Filtertext` varchar(2048) NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

CREATE TABLE `User_Path` (
  `User_Path_ID` int(11) NOT NULL,
  `User_ID` int(11) NOT NULL,
  `Path` varchar(1024) NOT NULL,
  `root` tinyint(4) DEFAULT 0
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;


CREATE TABLE `Verb_Vorlagen_Eigenschaften` (
  `Vorl_Eigens_ID` int(11) NOT NULL,
  `Vorlageneigenschaften_ID` int(11) NOT NULL,
  `Elementvorlage_ID` int(11) NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;


CREATE TABLE `Vorlagen` (
  `Vorlage_ID` int(11) NOT NULL,
  `DE` varchar(255) DEFAULT NULL,
  `EN` varchar(255) DEFAULT NULL,
  `NL` varchar(255) DEFAULT NULL,
  `FR` varchar(255) DEFAULT NULL,
  `Typ` varchar(10) DEFAULT NULL COMMENT 'Dokument oder Element',
  `Eigenschaften` blob DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=latin1 COLLATE=latin1_swedish_ci;


CREATE TABLE `Vorlageneigenschaften` (
  `Vorlageneigenschaften_ID` int(10) NOT NULL,
  `Reihenfolge` int(11) NOT NULL,
  `Attributtyp` varchar(20) NOT NULL,
  `orig_Name` varchar(30) NOT NULL,
  `Eigenschaft` varchar(30) NOT NULL,
  `DE` varchar(30) NOT NULL,
  `Hinweis_DE` varchar(256) DEFAULT NULL,
  `EN` varchar(30) NOT NULL,
  `Hinweis_EN` varchar(256) DEFAULT NULL,
  `NL` varchar(30) NOT NULL,
  `Hinweis_NL` varchar(256) DEFAULT NULL,
  `FR` varchar(30) NOT NULL,
  `Hinweis_FR` varchar(256) DEFAULT NULL,
  `Standardwert` varchar(255) NOT NULL,
  `Darstellung_Dialog` varchar(20) DEFAULT NULL,
  `Tab` varchar(10) NOT NULL DEFAULT 'Tab 1',
  `Auswahl` tinyint(1) NOT NULL DEFAULT 1
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;


CREATE TABLE `Vorlageneigenschaften_Optionen` (
  `Vorlageneigenschaften_Optionen_ID` int(11) NOT NULL,
  `Vorlageneigenschaften_ID` int(11) NOT NULL,
  `Optionswert_DE` varchar(50) NOT NULL,
  `Optionswert_EN` varchar(50) NOT NULL,
  `Optionswert_NL` varchar(50) NOT NULL,
  `Optionswert_FR` varchar(50) NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

