CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`%` SQL SECURITY DEFINER VIEW `User_ID`  AS  select `User`.`User_ID` AS `User_ID` from `User` where (`User`.`UserName` = left(user(),(locate('@',user()) - 1)));CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`%` SQL SECURITY DEFINER VIEW `User_Pfade`  AS  select `User_Path`.`Path` AS `Path`,`User`.`UserName` AS `UserName` from (`User` join `User_Path`) where ((`User`.`User_ID` = `User_Path`.`User_ID`) and (`User_Path`.`User_ID` = (select `User`.`User_ID` AS `User_ID` from `User` where (`User`.`UserName` = left(user(),(locate('@',user()) - 1))))));
CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`%` SQL SECURITY DEFINER VIEW `User_Baum`  AS  select `Baum`.`Path` AS `Path`,`Baum`.`Baum_ID` AS `Baum_ID`,`Baum`.`Eltern_ID` AS `Eltern_ID`,`Baum`.`owner` AS `owner`,`Baum`.`Bezeichnung` AS `Bezeichnung`,`Baum`.`Vorlage` AS `Vorlage`,`Baum`.`Inhalt` AS `Inhalt` from (`Baum` join `User_Pfade`) where (`User_Pfade`.`Path` = left(`Baum`.`Path`,length(`User_Pfade`.`Path`)));
